function Line(id,list){
	let arr = [];
	for(let t = 0; t < list.length; t++){
		arr.push(list[t].name)
	};
	var e = echarts.init(document.getElementById(id)),
		a = {
			tooltip: {
				trigger: "axis"
			},
			grid: {
				x: 40,
				x2: 40,
				y2: 24
			},
			legend: {
				orient: "vertical",
				x: "left",
				data: arr
			},
			calculable: !0,
			xAxis: [{
				type: "category",
				boundaryGap: !1,
				nameTextStyle: {
					color: "#ffffff",
					fontSize: 8
				},
				color: "#ffffff",
				data: ["1", "2", "3", "4", "5", "6"]
			}],
			yAxis: [{
				type: "value",
				nameTextStyle: {
					color: "#ffffff",
					fontSize: 8
				},
				axisLabel: {
					formatter: "{value}"
				}
			}],
			series: list
		};
	e.setOption(a), $(window).resize(e.resize);
}
function Pie(id,list){
	let arr = [];
	for(let t = 0; t < list.length; t++){
		arr.push(list[t].name)
	};
	var l = echarts.init(document.getElementById(id)),
		u = {
			tooltip: {
				trigger: "item",
				// formatter: "{a} <br/>{b} : {c} ({d}%)"
				formatter: "{a} <br/>{b} ({d}%)"
			},
			legend: {
				orient: "vertical",
				x: "left",
				data: arr
			},
			calculable: !0,
			series: [{
				name: "个",
				type: "pie",
				radius: "55%",
				center: ["50%", "60%"],
				data: list
			}]
		};
	l.setOption(u), $(window).resize(l.resize);
}
function Piequ(id,list){
	let sizr = ['35%','55%'];
	if(list.sys){
		sizr = ['20%', '34%'];
		list = list.data
	}
	let arr = [];
	for(let t = 0; t < list.length; t++){
		arr.push(list[t].name)
	};
	var l = echarts.init(document.getElementById(id)),
		u = {
			tooltip: {
				trigger: "item",
				formatter: "{a} <br/>{b} : ({d}%)"
			},
			legend: {
				orient: "horizontal",
				x: "center",
				data: arr
			},
			calculable: !0,
			series: [{
				name: "个",
				type: "pie",
				radius: sizr,
				center: ["50%", "60%"],
				data: list
			}]
		};
	l.setOption(u), $(window).resize(l.resize);
}
